insert into Coursera.Accident_Reduced(Severity, Start_date, start_year, Start_Month, Start_day,  Start_hour, Temperature, Visibility, Wind_Speed, Precipitation)(select Severity, date(start_Time) as Start_date, year(start_time) as Start_year, month(Start_time) as Start_month, dayofweek(start_time) as start_day, hour(start_time) as start_hour, Temperature, Visibility, Wind_speed, Precipitation from Coursera.Accidents where ucase(state) in('SC'))
