insert into Coursera.Accident_Reduced(Severity, Start_date, Start_day, Start_hour, Temperature, Visibility, Wind_Speed, Precipitation)(select Severity, date(start_Time) as Start_date, dayofweek(start_time) as start_day, hour(start_time) as start_hour, Temperature, Visibility, Wind_speed, Precipitation from Coursera.Accidents where ucase(state) in('SC'))
